import React, { useState } from "react";
import Card from "react-bootstrap/Card"
import Col from "react-bootstrap/Col"
import Button from "react-bootstrap/Button"
import deleteMyData from "../../api-functions/my-data/deleteMyData";
import Modal from "react-bootstrap/Modal"


import "../../css/PersonalDataCard.css"
function UpdateModal() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <Button variant="warning" onClick={handleShow}>
                Update
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}


class PersonalDataCard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            show: true
        }
        this.handleClose = this.handleClose.bind(this)
        this.handleShow = this.handleShow.bind(this)
    }

    handleClose() { this.setState({ show: false }) };
    handleShow() { this.setState({ show: true }) };


    render() {
        return (
            <>
                <Col >
                    <div onClick={() => { this.handleShow() }}>
                        <Card className="card" border="warning" style={{ width: '15rem', margin: "1rem" }}>
                            <Card.Header>{this.props.header}</Card.Header>

                            <Card.Body>
                                <Card.Title>{this.props.value}</Card.Title>
                                <Card.Text>
                                    Self-Signed Cert
                                </Card.Text>
                                <Button variant="danger" onClick={() => deleteMyData(this.props.person, this.props.header, this.props.value)} >
                                    Delete
                                </Button>
                                <UpdateModal></UpdateModal>
                            </Card.Body>
                        </Card>
                    </div>
                </Col >

            </>


        )
    }
}

export default PersonalDataCard
